example.code:
package main

import (
	"fmt"
	"log"
	"os"
	"path/filepath"
	"github.com/asaidimu/ghupdate"
)

var Version = "v1.0.0" // Injected via ldflags

const (
	githubOwner  = "your-owner"
	githubRepo   = "your-repo"
	appName      = "your-app"
	assetPattern = "{appname}-{version}-{os}-{arch}{ext}" // Adapt to your naming
)

func main() {
	// Phase 1: Handle self-replacement if launched by ApplyUpdate
	if ghupdate.HandleUpdateMode() {
		fmt.Println("ðŸŽ‰ Update applied successfully! Resuming.")
	}

	// Phase 2: Determine data directory and clean up old temp files
	dataDir, err := os.UserCacheDir() // Or os.UserConfigDir()
	if err != nil {
		log.Fatalf("Error getting cache dir: %v", err)
	}
	appDataDir := filepath.Join(dataDir, appName)
	if err := os.MkdirAll(appDataDir, 0755); err != nil {
		log.Fatalf("Error creating app data dir: %v", err)
	}
	if err := ghupdate.CleanupUpdate(appDataDir); err != nil {
		log.Printf("Warning: Failed to clean up old update files: %v\n", err)
	}

	// Phase 3: Your application's core logic
	fmt.Printf("Running %s version %s\n", appName, Version)
	// Simulate work
	// time.Sleep(2 * time.Second)

	// Phase 4: Check for and apply updates (e.g., on startup or user command)
	executablePath, err := os.Executable()
	if err != nil {
		log.Printf("Error getting executable path: %v", err)
		return
	}

	config := ghupdate.UpdateConfig{
		GitHubOwner:    githubOwner,
		GitHubRepo:     githubRepo,
		GitHubToken:    os.Getenv("GITHUB_TOKEN"), // Optional for public repos
		CurrentVersion: Version,
		DataDir:        appDataDir,
		ExecutablePath: executablePath,
		AssetPattern:   assetPattern,
	}

	updateInfo, err := ghupdate.CheckAndPrepareUpdate(config)
	if err != nil {
		log.Printf("Error checking for update: %v\n", err)
		return
	}

	if updateInfo == nil {
		fmt.Println("No update available.")
		return
	}

	fmt.Printf("Update available: %s -> %s\n", updateInfo.CurrentVersion, updateInfo.LatestVersion)
	if err := ghupdate.ApplyUpdate(config); err != nil {
		log.Fatalf("Error applying update: %v\n", err)
	}
	// The program exits here if ApplyUpdate succeeds.
}

