```go
config := ghupdate.UpdateConfig{
    // Missing GitHubOwner
    GitHubRepo: "my-repo",
    CurrentVersion: "v1.0.0",
    DataDir: "/tmp/data",
    ExecutablePath: "/path/to/app",
    AssetPattern: "app-{version}-{os}-{arch}{ext}",
}
_, err := ghupdate.CheckAndPrepareUpdate(config)
// err will contain "invalid config: GitHubOwner is required"
```

```go
config := ghupdate.UpdateConfig{
    GitHubOwner: "my-owner",
    GitHubRepo: "my-repo",
    CurrentVersion: "v1.0.0",
    DataDir: "/tmp/data",
    ExecutablePath: "/path/to/app",
    // Missing AssetPattern
}
_, err := ghupdate.CheckAndPrepareUpdate(config)
// err will contain "invalid config: AssetPattern is required"
```

